import logo from './logo.svg';
import './App.css';
import React, {useRef, useState} from 'react';
import Avatar from 'antd/lib/avatar/avatar';

// 이미지를 위해 antd 라이브러리 설치함.
function App() {
  const [Image, setImage] = useState("https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png")
  const fileInput = useRef(null)
  const [name, setName] = useState('Lee')
 // let firstName = 'Lee';
  let lastName = 'jiyoon' ;
  // 
  const handleTitle = (e) => {
    setName(e.target.value)
  }

  return (
    <div className="App">
     <div className='myprofile'>
        <h3> My Profile </h3>
     </div>
     <hr/>
     <img className='profile_img'
     src = {Image}
     size = {200}
     onClick = {() => {fileInput.current.click()}}/>

    <div className='profile_info'>
     <h4>Team name</h4>
     <h5>산기대</h5> {/* DB에서 데이터 받아와서 넣을 예정*/}
     <h4>Email</h4>
     <h5>이메일</h5>
    </div>

    <div className='profile_name'>
      <h4>First Name</h4>
      <input type = "text" name = "name" className='firstName' onChange = {handleTitle} value={name} ></input>
      <h4>Last Name</h4>
      <input type = 'writer' className='lastName' value={lastName} ></input>
    </div>
    </div>
  );
}

export default App;
