import logo from './logo.svg';
import './App.css';
import React, {useRef, useState} from 'react';
import { PassChag } from './modals/PassChag';
import { ProfileChag } from './modals/ProfileChag';

function App() {
  const [Image, setImage] = useState("https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png")
  const fileInput = useRef(null)
  const [firstname, setfirstName] = useState('Lee')
  const [lastname, setlastName] = useState('jiyoon')
  const [Passmodal, Passmodalset] = useState(false)
  const [Profilemodal, Profilemodalset] = useState(false)

  const getData = (Image) => {
    setImage(Image);
  }

  const handleFirstname = (e) => {
    setfirstName(e.target.value)
  }

  const handleLastname = (e) => {
    setlastName(e.target.value)
  }

  const Trim = () => {
    if(firstname.length < 1) {
      alert('Please enter your name');
    }
  }



  return (
    <div className="App">
      <PassChag
      show = {Passmodal}
      onHide={ () => Passmodalset(false)}
      />
      <ProfileChag
      show = {Profilemodal}
      onHide={ () => Profilemodalset(false)}
      Image={Image}
      getData = {getData}
      />

     <div className='myprofile'>
        <h3> My Profile </h3>
     </div>
     <hr/>
     <div>
     <img className='profile_img'
     src = {Image}
     size = {200}
     onClick = {() => Profilemodalset(true) }/>
     </div>
    <div className='profile_info'>
     <h4>Team name</h4>
     <h5>산기대</h5> {/* DB에서 데이터 받아와서 넣을 예정*/}
     <h4>Email</h4>
     <h5>이메일</h5>
    </div>

    <div className='profile_name'>
      <h4>First Name</h4>
      <input className='firstName' onChange = {handleFirstname} value={firstname} type = "text"></input>
      <span className='underline'></span>
      <h4>Last Name</h4>
      <input type = 'writer' className='lastName' onChange = {handleLastname} value={lastname} ></input>
      <span className='underline2'></span>
    </div>

    <div className = "save">
      <button className = "save_button" onClick={Trim}> Save </button>
    </div>
    <div className='change_passwd'>
     <h4>Password</h4>
     <p>You can set a new Password</p>
     <button className='change_pw_bt' onClick={() => Passmodalset(true)}> Change Password </button>
    </div>
  </div>
  );
}

export default App;
